	 const params = this.props.location.state;
	 	var img_src = params.show.image.original
		return(
			<div>
			<div className="detail_intro">
			<img src={img_src} className="detail_img" />
			</div>
			<div class="detail_summary">
			</div>
			</div>
		);

}

}

export default Detail;
