// components/Film.js
 import React from 'react';
 import PropTypes from 'prop-types';


/*
const Movie = props => (
  <div className="movie">
    <figure className="movie__figure">
      <img src={img_src} className="movie__poster" />
      <h2 className="movie__title">{props.show.name}</h2>
    </figure>
  </div>
);
*/

const Movie = function(props)  {
  var img_src ;
if(props.show.image){
  img_src= props.show.image.medium
}else{
  img_src= `https://www.google.com/search?q=no+picture&source=lnms&tbm=isch&sa=X&ved=0ahUKEwjHtpfR6rfiAhXNxqQKHc0RBpMQ_AUIDigB&biw=1280&bih=672#imgrc=zK3_S7lOQ0LI6M:`;
}
  return (
    <div className="movie">
      <figure className="movie__figure">
        <img src={img_src} className="movie__poster" />
        <h2 className="movie__title">{props.show.name}</h2>
      </figure>
    </div>
    );
}  


Movie.propTypes = {
  id         : PropTypes.number.isRequired,
  name      : PropTypes.string.isRequired,
  poster_path: PropTypes.string,
  vote_average: PropTypes.number,
};

export default Movie;

